select
    hsi.staff_info_id
    ,ss.name 工作网点
    ,if(hsi.is_sub_staff = 0 , '否', '是') 是否子账号
    ,hsa.staff_info_id 主账号
    ,if(hsi.is_sub_staff = 1, ss2.name, null) 原所属网点
    ,count(pi.pno)/count(distinct date(convert_tz(pi.finished_at, '+00:00', '+07:00'))) avg_pnos
from bi_pro.hr_staff_info hsi
join fle_staging.parcel_info pi on pi.ticket_delivery_staff_info_id = hsi.staff_info_id
